package practice;

public class Ifelsecondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// if else condition / code 1 / if part true
int a, b;
 a = 40; b = 20;
		
if (a>b)
{
 System.out.println("a is grater than b: if part");	// if part is true
}
else
{ 
	System.out.println("else part is executed");
}

// if else condition / code 2/ else part is true
if (!(a>b)) // we have used ! operator to make it false
{
 System.out.println("a is grater than b: if part");	// if part is true
}
else
{ 
	System.out.println("else part is executed");


	}

}
}
